import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <motion.section
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="my-16 text-center"
    >
      <h2 className="text-4xl md:text-6xl font-bold mb-4">Hi, I'm Piyush Unde</h2>
      <p className="text-xl md:text-2xl text-gray-700 max-w-xl mx-auto">
        Proficient in C++ and Python with strong web development skills using ReactJS, Node.js, and modern tools. Passionate about building interactive web applications.
      </p>
    </motion.section>
  );
};

export default Hero;
