import React from 'react';
import { motion } from 'framer-motion';
import { FaEnvelope, FaPhone } from 'react-icons/fa';

const Contact = () => {
  return (
    <motion.section
      id="contact"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 1 }}
      className="mb-16 text-center"
    >
      <h3 className="text-3xl font-semibold mb-6 border-b-2 border-blue-500 inline-block">Contact</h3>
      <p className="mb-4 text-lg">Reach out via email or phone</p>
      <p className="flex justify-center items-center space-x-2 text-blue-600">
        <FaEnvelope /> <a href="mailto:piyushunde87@gmail.com">piyushunde87@gmail.com</a>
      </p>
      <p className="flex justify-center items-center space-x-2 text-blue-600 mt-2">
        <FaPhone /> <span>8805791980</span>
      </p>
    </motion.section>
  );
};

export default Contact;
