import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaCode, FaDatabase, FaTools, FaGlobe, FaLaptopCode } from 'react-icons/fa';

const skills = {
  Languages: [
    { name: 'C++', level: 90, icon: '⚡' },
    { name: 'Python', level: 85, icon: '🐍' },
    { name: 'JavaScript', level: 80, icon: '🟨' }
  ],
  Frontend: [
    { name: 'HTML', level: 95, icon: '🌐' },
    { name: 'CSS', level: 90, icon: '🎨' },
    { name: 'Tailwind CSS', level: 85, icon: '💨' },
    { name: 'ReactJS', level: 80, icon: '⚛️' },
    { name: 'Next.js', level: 75, icon: '🚀' }
  ],
  Backend: [
    { name: 'Node.js', level: 80, icon: '🟢' },
    { name: 'Express.js', level: 75, icon: '📡' }
  ],
  Databases: [
    { name: 'MySQL', level: 85, icon: '🐬' },
    { name: 'MongoDB', level: 80, icon: '🍃' },
    { name: 'PL/SQL', level: 75, icon: '🗄️' }
  ],
  Tools: [
    { name: 'VS Code', level: 95, icon: '💻' },
    { name: 'Git', level: 85, icon: '📝' },
    { name: 'GitHub', level: 90, icon: '🐙' }
  ]
};

const categoryIcons = {
  Languages: FaCode,
  Frontend: FaGlobe,
  Backend: FaLaptopCode,
  Databases: FaDatabase,
  Tools: FaTools
};

const Skills = () => {
  const [hoveredSkill, setHoveredSkill] = useState(null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const categoryVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  const skillVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 0.4 }
    }
  };

  return (
    <motion.section
      id="skills"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
      className="mb-16"
    >
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8 }}
        className="text-center mb-12"
      >
        <h3 className="text-4xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          Skills & Expertise
        </h3>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          A comprehensive overview of my technical skills and proficiency levels across different domains.
        </p>
      </motion.div>

      <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8 max-w-7xl mx-auto">
        {Object.entries(skills).map(([category, skillList]) => {
          const IconComponent = categoryIcons[category];
          
          return (
            <motion.div
              key={category}
              variants={categoryVariants}
              whileHover={{ y: -5 }}
              className="bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 overflow-hidden group"
            >
              {/* Category Header */}
              <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6">
                <div className="flex items-center gap-3">
                  <IconComponent size={24} />
                  <h4 className="text-xl font-bold">{category}</h4>
                </div>
                <p className="text-blue-100 text-sm mt-2">
                  {skillList.length} skill{skillList.length !== 1 ? 's' : ''}
                </p>
              </div>

              {/* Skills List */}
              <div className="p-6">
                <div className="space-y-4">
                  {skillList.map((skill, index) => (
                    <motion.div
                      key={skill.name}
                      variants={skillVariants}
                      onHoverStart={() => setHoveredSkill(skill.name)}
                      onHoverEnd={() => setHoveredSkill(null)}
                      className="group/skill"
                    >
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <span className="text-lg">{skill.icon}</span>
                          <span className="font-medium text-gray-700 group-hover/skill:text-blue-600 transition-colors">
                            {skill.name}
                          </span>
                        </div>
                        <span className="text-sm font-semibold text-gray-500">
                          {skill.level}%
                        </span>
                      </div>
                      
                      {/* Skill Bar */}
                      <div className="w-full bg-gray-200 rounded-full h-2 overflow-hidden">
                        <motion.div
                          initial={{ width: 0 }}
                          whileInView={{ width: `${skill.level}%` }}
                          viewport={{ once: true }}
                          transition={{ duration: 1, delay: index * 0.1 }}
                          className={`h-full rounded-full transition-all duration-300 ${
                            hoveredSkill === skill.name
                              ? 'bg-gradient-to-r from-blue-500 to-purple-500'
                              : 'bg-gradient-to-r from-blue-400 to-purple-400'
                          }`}
                        />
                      </div>
                    </motion.div>
                  ))}
                </div>
              </div>
            </motion.div>
          );
        })}
      </div>

      {/* Additional Skills Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8, delay: 0.5 }}
        className="mt-16 text-center"
      >
        <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-8 max-w-4xl mx-auto">
          <h4 className="text-2xl font-bold text-gray-800 mb-4">
            Always Learning & Growing
          </h4>
          <p className="text-gray-600 mb-6">
            I'm constantly expanding my skill set and exploring new technologies to stay current with industry trends.
          </p>
          <div className="flex flex-wrap justify-center gap-3">
            {['TypeScript', 'GraphQL', 'Docker', 'AWS', 'Machine Learning', 'DevOps'].map((skill, index) => (
              <motion.span
                key={skill}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ delay: index * 0.1 }}
                whileHover={{ scale: 1.1, rotate: 5 }}
                className="bg-white px-4 py-2 rounded-full text-sm font-medium text-gray-700 shadow-md hover:shadow-lg transition-all duration-300 border border-gray-100"
              >
                {skill}
              </motion.span>
            ))}
          </div>
        </div>
      </motion.div>
    </motion.section>
  );
};

export default Skills;
