import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Projects = () => {
  const [projects, setProjects] = useState([
    // Initial example project (can be empty)
    // { id: 1, name: 'Sample Project', description: 'Brief description', link: 'https://example.com' }
  ]);
  const [formData, setFormData] = useState({ name: '', description: '', link: '' });

  // Handle input changes dynamically
  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add new project
  const handleAdd = e => {
    e.preventDefault();
    if (!formData.name || !formData.description) return alert('Please fill in project name and description');
    const newProject = { id: Date.now(), ...formData };
    setProjects([newProject, ...projects]);
    setFormData({ name: '', description: '', link: '' });
  };

  // Remove project by id
  const handleRemove = id => {
    setProjects(projects.filter(p => p.id !== id));
  };

  return (
    <motion.section
      id="projects"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 1 }}
      className="mb-16"
    >
      <h3 className="text-3xl font-semibold mb-6 border-b-2 border-blue-500 inline-block">Projects</h3>

      {/* Project add form */}
      <form onSubmit={handleAdd} className="mb-8 bg-white p-6 rounded shadow max-w-xl mx-auto">
        <h4 className="text-xl font-bold mb-4">Add a New Project</h4>
        <input
          type="text"
          name="name"
          placeholder="Project Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 mb-3 border border-gray-300 rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Project Description"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-2 mb-3 border border-gray-300 rounded"
          rows={3}
          required
        />
        <input
          type="url"
          name="link"
          placeholder="Project Link (optional)"
          value={formData.link}
          onChange={handleChange}
          className="w-full p-2 mb-3 border border-gray-300 rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 transition text-white px-4 py-2 rounded"
        >
          Add Project
        </button>
      </form>

      {/* Project list */}
      {projects.length === 0 && <p className="text-center italic text-gray-600">No projects added yet.</p>}
      <div className="space-y-8 max-w-3xl mx-auto">
        {projects.map(({ id, name, description, link }) => (
          <motion.div
            key={id}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white p-6 rounded shadow relative"
          >
            <h4 className="text-2xl font-semibold mb-2">{name}</h4>
            <p className="mb-2">{description}</p>
            {link && (
              <a href={link} target="_blank" rel="noreferrer" className="text-blue-600 underline">
                Visit Project
              </a>
            )}
            <button
              onClick={() => handleRemove(id)}
              aria-label={`Remove project ${name}`}
              className="absolute top-3 right-3 text-red-600 hover:text-red-800 font-bold"
            >
              &times;
            </button>
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
};

export default Projects;
