import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaDownload, FaEnvelope, FaArrowDown } from 'react-icons/fa';

const Hero = () => {
  const [isHovered, setIsHovered] = useState(false);

  const textVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        staggerChildren: 0.2
      }
    }
  };

  const letterVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  const floatingVariants = {
    animate: {
      y: [-10, 10, -10],
      transition: {
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <motion.section
      initial="hidden"
      animate="visible"
      className="relative min-h-screen flex items-center justify-center text-center overflow-hidden"
    >
      {/* Background floating elements */}
      <motion.div
        variants={floatingVariants}
        animate="animate"
        className="absolute top-20 left-10 w-20 h-20 bg-blue-200 rounded-full opacity-20"
      />
      <motion.div
        variants={floatingVariants}
        animate="animate"
        style={{ animationDelay: '1s' }}
        className="absolute top-40 right-20 w-16 h-16 bg-purple-200 rounded-full opacity-20"
      />
      <motion.div
        variants={floatingVariants}
        animate="animate"
        style={{ animationDelay: '2s' }}
        className="absolute bottom-40 left-20 w-12 h-12 bg-green-200 rounded-full opacity-20"
      />

      <div className="relative z-10 max-w-4xl mx-auto px-4">
        <motion.div variants={textVariants}>
          {/* Greeting */}
          <motion.h3 
            variants={letterVariants}
            className="text-xl md:text-2xl text-blue-600 font-medium mb-4"
          >
            Hello, I'm
          </motion.h3>

          {/* Name */}
          <motion.h1 
            variants={letterVariants}
            className="text-5xl md:text-7xl font-bold mb-6 bg-gradient-to-r from-blue-600 via-purple-600 to-green-600 bg-clip-text text-transparent"
            onHoverStart={() => setIsHovered(true)}
            onHoverEnd={() => setIsHovered(false)}
          >
            Piyush Unde
          </motion.h1>

          {/* Title */}
          <motion.h2 
            variants={letterVariants}
            className="text-2xl md:text-3xl text-gray-700 mb-6"
          >
            Full Stack Developer & Problem Solver
          </motion.h2>

          {/* Description */}
          <motion.p 
            variants={letterVariants}
            className="text-lg md:text-xl text-gray-600 max-w-2xl mx-auto mb-8 leading-relaxed"
          >
            Proficient in <span className="font-semibold text-blue-600">C++</span> and <span className="font-semibold text-green-600">Python</span> with strong web development skills using <span className="font-semibold text-purple-600">ReactJS</span>, <span className="font-semibold text-orange-600">Node.js</span>, and modern tools. Passionate about building interactive web applications that solve real-world problems.
          </motion.p>

          {/* Interactive Buttons */}
          <motion.div 
            variants={letterVariants}
            className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-12"
          >
            <motion.button
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('contact')}
              className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-8 py-4 rounded-full font-semibold text-lg shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-3"
            >
              <FaEnvelope />
              Get In Touch
            </motion.button>

            <motion.button
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('projects')}
              className="bg-white hover:bg-gray-50 text-gray-800 px-8 py-4 rounded-full font-semibold text-lg border-2 border-gray-200 hover:border-gray-300 shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-3"
            >
              <FaArrowDown />
              View My Work
            </motion.button>
          </motion.div>

          {/* Skills Preview */}
          <motion.div 
            variants={letterVariants}
            className="flex flex-wrap justify-center gap-3 mb-8"
          >
            {['React', 'Node.js', 'Python', 'C++', 'MongoDB', 'TypeScript'].map((skill, index) => (
              <motion.span
                key={skill}
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: index * 0.1 }}
                whileHover={{ scale: 1.1, rotate: 5 }}
                className="bg-white px-4 py-2 rounded-full text-sm font-medium text-gray-700 shadow-md hover:shadow-lg transition-all duration-300 border border-gray-100"
              >
                {skill}
              </motion.span>
            ))}
          </motion.div>
        </motion.div>

        {/* Scroll indicator */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2 }}
          className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
        >
          <motion.div
            animate={{ y: [0, 10, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
            className="w-6 h-10 border-2 border-gray-400 rounded-full flex justify-center"
          >
            <motion.div
              animate={{ y: [0, 12, 0] }}
              transition={{ duration: 2, repeat: Infinity }}
              className="w-1 h-3 bg-gray-400 rounded-full mt-2"
            />
          </motion.div>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Hero;
