import React from 'react';
import { motion } from 'framer-motion';

const skills = {
  Languages: ['C++', 'Python', 'JavaScript'],
  Frontend: ['HTML', 'CSS', 'Tailwind CSS', 'ReactJS', 'Next.js'],
  Backend: ['Node.js', 'Express.js'],
  Databases: ['MySQL', 'MongoDB', 'PL/SQL'],
  Tools: ['VS Code', 'Git', 'GitHub']
};

const Skills = () => {
  return (
    <motion.section
      id="skills"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 1 }}
      className="mb-16"
    >
      <h3 className="text-3xl font-semibold mb-6 border-b-2 border-blue-500 inline-block">Skills</h3>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {Object.entries(skills).map(([category, skillList]) => (
          <div key={category} className="bg-white p-6 rounded shadow">
            <h4 className="font-bold text-xl mb-2">{category}</h4>
            <ul className="list-disc list-inside space-y-1">
              {skillList.map(skill => (
                <li key={skill}>{skill}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </motion.section>
  );
};

export default Skills;
